<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Prod_cost{  	private function min_val($prod){		for ($i=0; $i < count($prod); $i++) {			if($prod[$i]['price']!=0)				$aux[$i] = $prod[$i]['price'];		}		if(isset($aux)){			$min = min($aux);			$key = array_search($min, $aux);			$return = array(				'discount_perc'	=>$prod[$key]['discount_perc'],				'discount'		=>$prod[$key]['discount'],				'fk_provider'	=>$prod[$key]['fk_provider'],				'price'			=>$prod[$key]['price']				);		}		else {			$return = array(				'discount_perc'	=> 0,				'discount'		=> 0,				'fk_provider'	=> 0,				'price'			=> 0				);		}		return $return;	}}